mapper.select:
SELECT ROWNUM rn , INS_LOT_ID , INS_ITEM_ID , INS_EMP_ID , INS_CT_ID , INS_PI_ID , INS_LOT_SIZE , TO_CHAR(INS_START_DATE, 'YYYY-MM-DD') AS INS_START_DATE , TO_CHAR(INS_END_DATE, 'YYYY-MM-DD') AS INS_END_DATE FROM INSTRUCTION WHERE 1=1
AND INS_START_DATE >= #{startDate}
AND #{endDate} >= INS_END_DATE
AND INS_ITEM_ID = #{find_item_process}
ORDER BY rn ASC

mapper.insert:
insert into INSTRUCTION (
                          INS_LOT_ID
                        , INS_ITEM_ID
                        , INS_EMP_ID
                        , INS_CT_ID
                        , INS_PI_ID
                        , INS_LOT_SIZE
                        , INS_START_DATE
                        , INS_END_DATE
                        )
                        values (
                          #{ins_lot_id}
                        , #{ins_item_id}
                        , #{ins_emp_id}
                        , #{ins_ct_id}
                        , #{ins_pi_id}
                        , #{ins_lot_size}
                        , #{ins_start_date}
                        , #{ins_end_date}
                        )

mapper.select:
SELECT ROWNUM rn ,CT_ID ,CT_ITEM_ID ,CT_QUANTITY ,TO_CHAR(CT_DATE, 'YYYY-MM-DD') AS CT_DATE ,TO_CHAR(CT_OB_DATE, 'YYYY-MM-DD') AS CT_OB_DATE FROM CONTRACT WHERE CT_DIV='수주'
AND CT_DATE BETWEEN #{ctStartDate} AND #{ctEndDate}
ORDER BY rn ASC

SELECT DISTINCT P_LOT_ID FROM PROCESS WHERE 1=1
AND P_LOT_ID = #{pLotId}
ORDER BY P_LOT_ID ASC

SELECT DISTINCT P_PI_ID FROM PROCESS WHERE 1=1
AND P_PI_ID = #{pPiId}
ORDER BY P_PI_ID ASC

SELECT ROWNUM AS rn, P_LOT_ID, P_PI_ID, P_B_ITEM_ID, P_PLAN_QUANTITY, P_ITEM_QUANTITY, P_DEFECTIVE_QUANTITY, TO_CHAR(P_START_DATE, 'YYYY-MM-DD') AS P_START_DATE, P_END_DATE, P_STATUS, P_NOTE FROM PROCESS WHERE 1=1
AND P_START_DATE >= #{startDate}
AND #{endDate} >= P_START_DATE
AND P_PI_ID = #{pPiId}
AND P_LOT_ID = #{pLotId}
ORDER BY rn ASC

mapper.insert:
insert into PROCESS (
                      P_LOT_ID
                    , P_PI_ID
                    , P_B_ITEM_ID
                    , P_PLAN_QUANTITY
                    , P_NOTE
                    )
                    values (
                      #{p_lot_id}
                    , #{p_pi_id}
                    , #{p_b_item_id}
                    , #{p_plan_quantity}
                    , #{p_note}
                    )

mapper.select:
SELECT ROWNUM rn , PI_ID , PI_MACHINE_ID , PI_NAME , PI_SEQ FROM PROCESS_INFO WHERE 1=1
AND PI_ID = #{piId}
ORDER BY rn ASC

mapper.insert:
INSERT INTO PROCESS_INFO (
                      PI_ID
                    , PI_MACHINE_ID
                    , PI_NAME
                    , PI_SEQ
            ) VALUES(
                      #{pi_id}
                    , #{pi_machine_id}
                    , #{pi_name}
                    , #{pi_seq}
                    )
