mapper.select:
SELECT ROWNUM rn , INS_LOT_ID , INS_ITEM_ID , INS_EMP_ID , INS_CT_ID , INS_PI_ID , INS_LOT_SIZE , TO_CHAR(INS_START_DATE, 'YYYY-MM-DD') AS INS_START_DATE , TO_CHAR(INS_END_DATE, 'YYYY-MM-DD') AS INS_END_DATE FROM INSTRUCTION WHERE 1=1
AND INS_START_DATE >= #{startDate}
AND #{endDate} >= INS_END_DATE
AND INS_ITEM_ID = #{find_item_process}
ORDER BY rn ASC

mapper.insert:
insert into INSTRUCTION (
                          INS_LOT_ID
                        , INS_ITEM_ID
                        , INS_EMP_ID
                        , INS_CT_ID
                        , INS_PI_ID
                        , INS_LOT_SIZE
                        , INS_START_DATE
                        , INS_END_DATE
                        )
                        values (
                          #{ins_lot_id}
                        , #{ins_item_id}
                        , #{ins_emp_id}
                        , #{ins_ct_id}
                        , #{ins_pi_id}
                        , #{ins_lot_size}
                        , #{ins_start_date}
                        , #{ins_end_date}
                        )

mapper.select:
SELECT ROWNUM rn ,CT_ID ,CT_ITEM_ID ,CT_QUANTITY ,TO_CHAR(CT_DATE, 'YYYY-MM-DD') AS CT_DATE ,TO_CHAR(CT_OB_DATE, 'YYYY-MM-DD') AS CT_OB_DATE FROM CONTRACT WHERE 1=1
AND CT_DATE BETWEEN #{ctStartDate} AND #{ctEndDate}
ORDER BY rn ASC
