mapper.select:
SELECT ROWNUM AS RN, PICTC.P_LOT_ID AS P_LOT_ID, PICTC.INS_LOT_ID AS INS_LOT_ID, PICTC.P_PI_ID AS P_PI_ID, PICTC.PI_NAME AS PI_NAME, PICTC.P_B_ITEM_ID AS P_B_ITEM_ID, PICTC.I_NAME AS I_NAME, PICTC.C_ID AS C_ID, PICTC.C_NAME AS C_NAME, PICTC.P_START_DATE AS P_START_DATE, PICTC.P_END_DATE AS P_END_DATE, PICTC.P_STATUS AS P_STATUS, PICTC.P_PLAN_QUANTITY AS P_PLAN_QUANTITY, PICTC.P_ITEM_QUANTITY AS P_ITEM_QUANTITY, PICTC.P_DEFECTIVE_QUANTITY AS P_DEFECTIVE_QUANTITY, PICTC.CT_UNIT AS CT_UNIT FROM (SELECT DISTINCT pr.P_PI_ID, pr.P_LOT_ID, ins.INS_LOT_ID, pr.P_B_ITEM_ID, pi.PI_NAME, it.I_NAME, co.C_ID, co.C_NAME, TO_CHAR(pr.P_START_DATE, 'YYYY-MM-DD') AS P_START_DATE, TO_CHAR(pr.P_END_DATE, 'YYYY-MM-DD') AS P_END_DATE, pr.P_STATUS, pr.P_PLAN_QUANTITY, pr.P_ITEM_QUANTITY, pr.P_DEFECTIVE_QUANTITY, UPPER(ct.CT_UNIT) AS CT_UNIT FROM PROCESS pr JOIN PROCESS_INFO pi ON pr.P_PI_ID = pi.PI_ID JOIN INSTRUCTION ins ON pr.P_LOT_ID = ins.INS_LOT_ID JOIN CONTRACT ct ON ins.INS_CT_ID = ct.CT_ID JOIN COMPANY co ON ct.CT_Company_ID = co.C_ID JOIN ITEM it ON pr.P_B_ITEM_ID = it.I_ID ORDER BY P_START_DATE desc, pr.P_LOT_ID, pr.P_PI_ID*1, pr.P_B_ITEM_ID) PICTC
AND C_ID = #{find_work_company}
AND P_B_ITEM_ID = #{find_work_item}
AND P_START_DATE >= #{workStartDate}
AND #{workEndDate} >= P_END_DATE
AND INS_LOT_ID = #{find_work_instruction}
AND P_PI_ID = #{find_work_processInfo}
AND 1=0

SELECT ROWNUM AS rn, WORK.W_ID , WORK.W_LOT_ID , WORK.W_DATE, WORK.E_DP_NAME, WORK.W_PI_ID, WORK.PI_NAME, WORK.MI_NAME, WORK.POSITION, WORK.INS_ITEM_ID, WORK.I_NAME, WORK.W_STATUS, WORK.I_STANDARD, WORK.CT_UNIT, WORK.W_TIME, WORK.W_PLAN_QUANTITY, WORK.W_ITEM_QUANTITY FROM (SELECT DISTINCT w.W_ID AS W_ID, w.W_LOT_ID AS W_LOT_ID, TO_CHAR(w.W_START_TIME, 'YYYY-MM-DD') AS W_DATE, e.E_DP_NAME, w.W_PI_ID, pi.PI_NAME, mi.MI_NAME, pi.PI_SEQ || '-' || mi.MI_POSITION AS POSITION, ins.INS_ITEM_ID, it.I_NAME, w.W_STATUS, it.I_STANDARD, UPPER(ct.CT_UNIT) AS CT_UNIT, w.W_TIME, w.W_PLAN_QUANTITY, w.W_ITEM_QUANTITY FROM WORK w JOIN WORKER wr ON w.W_ID = wr.WR_WORK_ID JOIN PROCESS pr ON w.W_LOT_ID = pr.P_LOT_ID JOIN PROCESS_INFO pi ON w.W_PI_ID = pi.PI_ID JOIN MACHINE_INFO mi ON pi.PI_MACHINE_ID = mi.mi_id JOIN INSTRUCTION ins ON w.W_LOT_ID = ins.INS_LOT_ID JOIN CONTRACT ct ON ins.INS_CT_ID = ct.CT_ID JOIN ITEM it ON pr.P_B_ITEM_ID = it.I_ID JOIN EMPLOYEE e ON wr.WR_EMP_ID = e.E_ID ORDER BY W_DATE) WORK
AND INS_ITEM_ID = #{find_work_item}
AND W_DATE >= #{workStartDate}
AND #{workEndDate} >= W_DATE
AND W_LOT_ID = #{find_work_instruction}
AND W_PI_ID = #{find_work_processInfo}
AND W_ID = #{find_work_id}
AND 1=0

SELECT ROWNUM AS RN, WORK.W_ID, WORK.W_LOT_ID, WORK.W_DATE, WORK.E_DP_NAME, WORK.allEmpList as allEmpList, WORK.substrAllEmp as substrAllEmp, WORK.W_PI_ID, WORK.PI_NAME, WORK.MI_NAME, WORK.POSITION, WORK.INS_ITEM_ID, WORK.I_NAME, WORK.W_STATUS, WORK.I_STANDARD, WORK.CT_UNIT, WORK.W_TIME, WORK.W_PLAN_QUANTITY, WORK.W_ITEM_QUANTITY, WORK.w_dft_quantity FROM (SELECT DISTINCT w.W_ID AS W_ID, w.W_LOT_ID AS W_LOT_ID, TO_CHAR(w.W_START_TIME, 'YYYY-MM-DD') AS W_DATE, e.E_DP_NAME, LISTAGG(DISTINCT wr.WR_EMP_ID || '(' || e.E_NAME ||')', ', ' ON OVERFLOW TRUNCATE '...') WITHIN GROUP(ORDER BY wr.WR_EMP_ID) OVER(PARTITION BY e.E_DP_NAME, w.W_ID) AS allEmpList, Substr(LISTAGG(DISTINCT wr.WR_EMP_ID || '(' || e.E_NAME ||')', ', ' ON OVERFLOW TRUNCATE '...') WITHIN GROUP(ORDER BY wr.WR_EMP_ID) OVER(PARTITION BY e.E_DP_NAME, w.W_ID), 1, 40) || '...' AS substrallemp, w.W_PI_ID, pi.PI_NAME, mi.MI_NAME, pi.PI_SEQ || '-' || mi.MI_POSITION AS POSITION, ins.INS_ITEM_ID, it.I_NAME, w.W_STATUS, it.I_STANDARD, UPPER(ct.CT_UNIT) AS CT_UNIT, w.W_TIME, w.W_PLAN_QUANTITY, w.W_ITEM_QUANTITY, w.w_dft_quantity FROM WORK w JOIN WORKER wr ON w.W_ID = wr.WR_WORK_ID JOIN PROCESS pr ON w.W_LOT_ID = pr.P_LOT_ID JOIN PROCESS_INFO pi ON w.W_PI_ID = pi.PI_ID JOIN MACHINE_INFO mi ON pi.PI_MACHINE_ID = mi.mi_id JOIN INSTRUCTION ins ON w.W_LOT_ID = ins.INS_LOT_ID JOIN CONTRACT ct ON ins.INS_CT_ID = ct.CT_ID JOIN ITEM it ON pr.P_B_ITEM_ID = it.I_ID JOIN EMPLOYEE e ON wr.WR_EMP_ID = e.E_ID ORDER BY W_DATE) WORK
AND INS_ITEM_ID = #{find_work_item}
AND W_DATE >= #{workStartDate}
AND #{workEndDate} >= W_DATE
AND W_LOT_ID = #{find_work_instruction}
AND W_PI_ID = #{find_work_processInfo}
AND W_ID = #{find_work_id}
AND 1=0

SELECT ROWNUM AS rn, mi.w_lot_id, mi.w_pi_id, mi.ins_item_id, mi.ct_company_id, mi.c_name, mi.inv_date, mi.b_material_id, mi.m_name, mi.b_unit, mi.inv_quantity FROM (SELECT DISTINCT w.W_LOT_ID, w.W_PI_ID, ins.INS_ITEM_ID , it.i_name, ct.CT_Company_ID, c.c_name, to_char(inv.INV_DATE,'yyyy-mm-dd') AS inv_date, b.B_MATERIAL_ID, m.M_NAME, upper(b.B_UNIT) AS B_UNIT, substr(inv.INV_QUANTITY,2) AS INV_QUANTITY FROM WORK w JOIN PROCESS_INFO pi ON w.W_PI_ID = pi.PI_ID JOIN INSTRUCTION ins ON w.W_LOT_ID = ins.INS_LOT_ID JOIN CONTRACT ct ON ins.INS_CT_ID = ct.CT_ID JOIN COMPANY c ON ct.CT_Company_ID = c.C_ID JOIN ITEM it ON ins.INS_ITEM_ID = it.I_ID JOIN BOM b ON ins.ins_ITEM_ID = b.B_ITEM_ID JOIN MATERIAL m ON b.B_MATERIAL_ID = m.M_ID JOIN INVENTORY inv ON w.W_PI_ID = inv.INV_pi_ID WHERE inv.INV_LOT_ID IS NOT NULL AND inv.INV_PI_ID IS NOT NULL AND inv.INV_QUANTITY<0 ORDER BY w.W_LOT_ID,w.W_PI_ID,b.B_MATERIAL_ID) mi
AND C_ID = #{find_work_company}
AND INS_ITEM_ID = #{find_work_item}
AND inv_date >= #{workStartDate}
AND #{workEndDate} >= inv_date
AND W_LOT_ID = #{find_work_instruction}
AND W_PI_ID = #{find_work_processInfo}
AND 1=0

SELECT ROWNUM AS rn, wr.e_dp_name, wr.e_name, wr.e_role, wr.wr_emp_id, wr.w_lot_id, wr.w_pi_id, wr.position, wr.wr_start_time, wr.wr_end_time FROM (SELECT e.E_DP_NAME, e.E_NAME , e.E_ROLE, wr.WR_EMP_ID, w.W_LOT_ID, w.W_PI_ID, pi.PI_SEQ || '-' || mi.MI_POSITION AS POSITION, to_char(wr.WR_START_TIME,'yyyy-mm-dd HH24:mi:ss') AS WR_START_TIME, to_char(wr.WR_END_TIME,'yyyy-mm-dd HH24:mi:ss') AS WR_END_TIME FROM WORK w JOIN WORKER wr ON w.W_ID = wr.WR_WORK_ID JOIN EMPLOYEE e ON wr.WR_EMP_ID = e.E_ID JOIN PROCESS_INFO pi ON w.W_PI_ID = pi.PI_ID JOIN MACHINE_INFO mi ON pi.PI_MACHINE_ID = mi.mi_id ORDER BY e.E_DP_NAME,WR_START_TIME, wr.WR_EMP_ID, w.W_PI_ID) wr
AND wr_start_time >= #{workStartDate}
AND #{workEndDate} >= wr_end_time
AND W_LOT_ID = #{find_work_instruction}
AND W_PI_ID = #{find_work_processInfo}
AND 1=0

SELECT ROWNUM AS rn, pimi.pi_id, pimi.pi_name, pimi.pi_machine_id, pimi.mi_name, pimi.position, pimi.mw_status, pimi.mw_condition FROM (SELECT pi.pi_id, pi.pi_name, pi.pi_machine_id, mi.mi_name, pi.PI_SEQ || '-' || mi.MI_POSITION AS POSITION, mw.MW_STATUS, mw.MW_CONDITION FROM process_info pi JOIN MACHINE_INFO mi ON pi.PI_MACHINE_ID = mi.mi_id JOIN MACHINE_WORK mw ON mw.MW_MI_ID = mi.mi_id ORDER BY pi.pi_id, pi.pi_machine_id) pimi
AND pi_id = #{find_work_processInfo}
AND 1=0

SELECT ROWNUM AS rn, WORK.W_ID , WORK.W_LOT_ID , WORK.W_DATE, WORK.E_DP_NAME, WORK.W_PI_ID, WORK.PI_NAME, WORK.MI_NAME, WORK.POSITION, WORK.INS_ITEM_ID, WORK.I_NAME, WORK.W_STATUS, WORK.I_STANDARD, WORK.CT_UNIT, WORK.W_TIME, WORK.W_PLAN_QUANTITY, WORK.W_ITEM_QUANTITY FROM (SELECT DISTINCT w.W_ID AS W_ID, w.W_LOT_ID AS W_LOT_ID, TO_CHAR(w.W_START_TIME, 'YYYY-MM-DD') AS W_DATE, e.E_DP_NAME, w.W_PI_ID, pi.PI_NAME, mi.MI_NAME, pi.PI_SEQ || '-' || mi.MI_POSITION AS POSITION, ins.INS_ITEM_ID, it.I_NAME, w.W_STATUS, it.I_STANDARD, UPPER(ct.CT_UNIT) AS CT_UNIT, w.W_TIME, w.W_PLAN_QUANTITY, w.W_ITEM_QUANTITY FROM WORK w JOIN WORKER wr ON w.W_ID = wr.WR_WORK_ID JOIN PROCESS pr ON w.W_LOT_ID = pr.P_LOT_ID JOIN PROCESS_INFO pi ON w.W_PI_ID = pi.PI_ID JOIN MACHINE_INFO mi ON pi.PI_MACHINE_ID = mi.mi_id JOIN INSTRUCTION ins ON w.W_LOT_ID = ins.INS_LOT_ID JOIN CONTRACT ct ON ins.INS_CT_ID = ct.CT_ID JOIN ITEM it ON pr.P_B_ITEM_ID = it.I_ID JOIN EMPLOYEE e ON wr.WR_EMP_ID = e.E_ID ORDER BY W_DATE) WORK WHERE WORK.W_DATE = to_char( sysdate, 'YYYY-MM-DD')
AND W_LOT_ID = #{find_work_instruction}
AND W_PI_ID = #{find_work_processInfo}
AND 1=0
