mapper.select:
SELECT ROWNUM AS listSeq, T."M_ID" AS matID, T."M_NAME" AS matName, T."M_USES" AS matUses, T."CT_ID" AS contractID, T."C_NAME" AS companyName, T."CT_UNIT" AS units, T."CT_QUANTITY" AS contractQuantity, T."INSPECTION_QUANTITY" AS inspectionQuantity, T."QS_DF_DIV1" AS qsDiv1, T."QS_DF_DIV2" AS qsDiv2, T."QI_DFT_QUANTITY" AS poorQuantity, (T."QI_DFT_QUANTITY" / T."INSPECTION_QUANTITY" * 100 ) AS defectRate, T."QI_PASSED_QUANTITY" AS goodQuantity, T."INSPECTION_DATE" AS inspectionDate, T."NOTES" AS notes FROM ( SELECT mat.M_ID, mat.M_NAME, mat.M_USES, con.CT_ID, comp.C_NAME, con.CT_UNIT, con.CT_QUANTITY, qi.QI_INSPECTION_QUANTITY AS "INSPECTION_QUANTITY", qs.QS_DF_DIV1, qs.QS_DF_DIV2, qi.QI_DFT_QUANTITY, qi.QI_PASSED_QUANTITY, qi.QI_DATE AS "INSPECTION_DATE", qi.QI_NOTE AS "NOTES" FROM CONTRACT con JOIN COMPANY comp ON con.CT_COMPANY_ID = comp.C_ID JOIN MATERIAL mat ON con.CT_MATERIAL_ID = mat.M_ID LEFT JOIN QUALITY_INSPECTION qi ON con.CT_ID = qi.QI_CT_ID AND qi.QI_CT_ID IS NOT NULL AND qi.QI_MATERIAL_ID IS NOT NULL LEFT JOIN QUALITY_STANDARD qs ON qi.QI_QS_ID = qs.QS_ID WHERE con.CT_DIV = '발주' AND qi.QI_CT_ID IS NOT NULL AND qi.QI_MATERIAL_ID IS NOT NULL
AND mat.M_ID = #{matID}
and con.CT_ID = #{contractID}
and qs.QS_DF_DIV1 = #{qsDiv1}
and qs.QS_DF_DIV2 = #{qsDiv2}
and qi.QI_DATE >= #{inspectionDate1}
and #{inspectionDate2} >= qi.QI_DATE
ORDER BY con.CT_ID ASC ) T
