mapper.select:
SELECT ROWNUM AS listSeq , T.WORK_ID as workID , T.PRO_LOT_ID as lotID , T.MI_ID as machineID , T.PI_ID as processID , T.PI_NAME as processName , T.CT_UNIT as unit , T.ITEM_QUANTITY as dailyWorkQuantity , T.INSPECTION_QUANTITY as inspectionQuantity , T.DFT_QUANTITY as defectQuantity , T.DEFECT_RATE as defectRate , T.PASSED_QUANTITY as goodQuantity , T.DF_DIV1 as qsDiv1 , T.DF_DIV2 as qsDiv2 , T.INSPECTION_DATE as inspectionDate , T.NOTE as note FROM ( SELECT qi.QI_WORK_ID AS WORK_ID , qi.QI_PRO_LOT_ID AS PRO_LOT_ID , mi.MI_ID AS MI_ID , qi.QI_PI_ID AS PI_ID , pi2.PI_NAME AS PI_NAME , ct.CT_UNIT AS CT_UNIT , w.W_ITEM_QUANTITY AS ITEM_QUANTITY , qi.QI_INSPECTION_QUANTITY AS INSPECTION_QUANTITY , qi.QI_DFT_QUANTITY AS DFT_QUANTITY , (qi.QI_DFT_QUANTITY / NULLIF(qi.QI_INSPECTION_QUANTITY, 0) * 100) AS DEFECT_RATE , qi.QI_PASSED_QUANTITY AS PASSED_QUANTITY , qs.QS_DF_DIV1 AS DF_DIV1 , qs.QS_DF_DIV2 AS DF_DIV2 , TO_CHAR(qi.QI_DATE, 'yyyy-mm-dd') AS INSPECTION_DATE , qi.QI_NOTE AS NOTE , ROW_NUMBER() OVER (PARTITION BY qi.QI_WORK_ID ORDER BY qi.QI_DATE DESC) AS rn FROM QUALITY_INSPECTION qi JOIN PROCESS_INFO pi2 ON qi.QI_PI_ID = pi2.PI_ID JOIN WORK w ON qi.QI_WORK_ID = w.W_ID JOIN MACHINE_INFO mi ON pi2.PI_MACHINE_ID = mi.MI_ID LEFT JOIN QUALITY_STANDARD qs ON qi.QI_QS_ID = qs.QS_ID JOIN INSTRUCTION ins ON w.W_LOT_ID = ins.INS_LOT_ID JOIN CONTRACT ct ON ins.INS_CT_ID = ct.CT_ID AND ct.CT_DIV = '수주' ) T WHERE T.rn = 1
and PRO_LOT_ID = #{lotID}
and PI_ID = #{processID}
and PI_NAME = #{processName}
and DF_DIV1 = #{qsDiv1}
and DF_DIV2 = #{qsDiv2}
and WORK_ID = #{workID}
and MI_ID = #{machineID}
ORDER BY INSPECTION_DATE DESC
